import shortid from './shortid'

import styles from './Statistic.module.css'

const id = shortid.generate();

const FeedbackOptions = ({ options, heandleGoodFeedback, heandleBadFeedback, heandleNeutralFeedback }) => {
    return (
        options.map(opt => (
            <button type="button" id={id} onClick={heandleGoodFeedback} className={styles.feedback_btn}>{opt}</button>
        ))
    )
}

export default FeedbackOptions;
